// Write your code here.
import {Component} from 'react'
import CoffeePlannerQuestion from '../CoffeePlannerQuestion'
import './index.css'

class CoffeePlanner extends Component {
  state = {showPlan: false}

  renderResponse = () => {
    const {
      DRINK_TYPE,
      COFFEE_TYPE,
      QUANTITY,
      GRIND_TYPE,
      DELIVER_TYPE,
    } = this.state

    if (
      DRINK_TYPE === undefined ||
      COFFEE_TYPE === undefined ||
      QUANTITY === undefined ||
      GRIND_TYPE === undefined ||
      DELIVER_TYPE === undefined
    ) {
      return 'Kindly select options for all the questions.'
    }

    return `I Drink my coffee as ${DRINK_TYPE}, with a ${COFFEE_TYPE} type of bean. ${QUANTITY} of ${GRIND_TYPE} ground, send to me ${DELIVER_TYPE}.`
  }

  addAnswer = answer => this.setState(prevState => ({...prevState, ...answer}))

  renderQuestions = () => {
    const {coffeePlanarList} = this.state
    return coffeePlanarList.map(question => (
      <CoffeePlannerQuestion key={question.id} question={question} />
    ))
  }

  onShowPlan = () =>
    this.setState(prevState => ({
      ...prevState,
      showPlan: true,
    }))

  render() {
    return (
      <div className="app-container">
        <div className="header-section">
          <h1 className="header-heading">Create a Plan</h1>
          <p className="header-description">
            We offer an assortment of the best artesian coffees from the globe
            delivered fresh to the door create your plan with this
          </p>
        </div>
        <div className="body-section">
          {renderQuestions()}
          <button type="button" onClick={this.onShowPlan}>
            Create my plan
          </button>
          {showPlan && <p className="plan">{renderResponse()}</p>}
        </div>
      </div>
    )
  }
}

export default CoffeePlanner
