import {Component} from 'react'
import './index.css'

class Filters extends Component {
  render() {
    const {
      categoryData,
      levelsData,
      updateCategory,
      updateLevel,
      selectedCategory,
      selectedLevel,
    } = this.props
    return (
      <div className="filters-container">
        <div className="filter-container">
          <p className="filter-label">LANGUAGE</p>
          <select className="filter-select">
            {categoryData.map(category => {
              const {id, language} = category
              return (
                <option
                  key={id}
                  onClick={() => updateCategory(language)}
                  selected={selectedCategory === langauge}
                >
                  {language}
                </option>
              )
            })}
          </select>
        </div>
        <div className="filter-container">
          <p className="filter-label">DIFFICULTY LEVEL</p>
          <select className="filter-select">
            {levelsData.map(category => {
              const {id, level} = category
              return (
                <option
                  key={id}
                  onClick={() => {
                    console.log(`${level} is selected`)

                    updateLevel(level)
                  }}
                  selected={selectedLevel === level}
                >
                  {level}
                </option>
              )
            })}
          </select>
        </div>
      </div>
    )
  }
}

export default Filters
